import img from "../assets/Pikachu.jpg";

//First Component content
function FirstComponent() {
  function Mybtn() {
    return <button> hellow I'm btn</button>;
  }

  //second Component content
  const user = {
    name: "Arief",
    imgUrl: img,
    imgSize: 90,
  };

  // rendering  list
  const products = [
    { title: "Mango", id: 1 },
    { title: "Orange", id: 2 },
    { title: "Apple", id: 3 },
    { title: "Banana", id: 4 },
  ];

  let items = products.map((product) => 
  <li >{product.title}</li>);

  return (
    <>
      <h1>Welcome all, I am {user.name} </h1>
      <Mybtn />

      {/* this is an second component */}
      <img
        src={user.imgUrl}
        style={{ width: user.imgSize, height: user.imgSize }}
      />

      {/* Renderring list */}
      <ol>{items}</ol>
      <ul>
        {items}
      </ul>
    </>
  );
}

export default FirstComponent;
